import logo from "./logo.svg";
import "./App.css";
import React, { Component } from "react";
import Modal from "./components/Modal";

const empleadoItems = [
  {
    empleado_id: 1,
    nombre: "Luis Vasquez",
    salario_base: "10,000",
    horas_trabajadas: "40",
    estado: false,
  },
  {
    empleado_id: 2,
    nombre: "Luis Enrique",
    salario_base: "14000",
    horas_trabajadas: "40",
    estado: true,
  },
  {
    empleado_id: 3,
    nombre: "Ana Rodriguez",
    salario_base: "12500",
    horas_trabajadas: "40",
    estado: true,
  },
];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      viewCompleted: false,
      empleadoList: empleadoItems,
      modal: false,
      activeItem: {
        nombre: "",
        salario_base: "",
        horas_trabajadas: "",
        estado: false,
      },
    };
  }

  toggle = () => {
    this.setState({ modal: !this.state.modal });
  };
  handleSubmit = (item) => {
    this.toggle();
    alert("save" + JSON.stringify(item));
  };
  handleDelete = (item) => {
    alert("delete" + JSON.stringify(item));
  };
  createItem = () => {
    this.setState({ activeItem: item, modal: !this.state.modal });
  };
  editItem = (item) => {
    this.setState({ activeItem: item, modal: !this.state.modal });
  };

  displayCompleted = (status) => {
    if (status) {
      return this.setState({ viewCompleted: true });
    }
    return this.setState({ viewCompleted: false });
  };

  renderTabList = () => {
    return (
      <div className="nav nav-tabs">
        <span
          className={this.state.viewCompleted ? "nav-link active:" : "nav-link"}
          onClick={() => this.displayCompleted(true)}
        >
          Activos
        </span>
        <span
          className={this.state.viewCompleted ? "nav-link" : "nav-link active"}
          onClick={() => this.displayCompleted(false)}
        >
          Inactivos
        </span>
      </div>
    );
  };
  renderItems = () => {
    const { viewCompleted } = this.state;
    const newItems = this.state.empleadoList.filter(
      (item) => item.estado == viewCompleted
    );

    return newItems.map((item) => (
      <li
        key={item.empleado_id}
        className="list-group-item d-flex justify-content-between align-items-center"
      >
        <span
          className={`empleado-nombre mr-2 ${
            this.state.viewCompleted ? "completed-todo" : ""
          }`}
          title={item.nombre}
        >
          {item.nombre}
        </span>
        <span>
          <button
            className="btn btn-secondary mr-2"
            onClick={() => this.editItem(item)}
          >
            Edit
          </button>
          <button
            className="btn btn-danger"
            onClick={() => this.handleDelete(item)}
          >
            Delete
          </button>
        </span>
      </li>
    ));
  };
  render() {
    return (
      <main className="container">
        <h1 className="text-write text-uppercase text-center my-4">
          Colaboradores:{" "}
        </h1>
        <div className="row">
          <div className="col-md-6 col-sm-10 mx-auto p-0">
            <div className="mb-4">
              <button className="btn btn-primary">Add Task</button>
            </div>
            {this.renderTabList()}
            <ul className="list-group list-group-flush border-top-0">
              {this.renderItems()}
            </ul>
          </div>
        </div>
      </main>
    );
  }
}
export default App;

